INSERT INTO `blogs` (`id`, `name`, `placelocation`, `placedesc`, `img`, `created_at`, `updated_at`) VALUES
(1, 'কার্জন হল', 'DHAKA UNIVERSITY', 'কার্জন হলটি মূলত একটি শহরের হল বলে মনে করা হয়েছিল, ভারতের ভারপ্রাপ্ত লর্ড কারজন যিনি 1904 সালে তার ভিত্তি স্থাপন করেছিলেন, তার নামকরণ করা হয়েছিল। এক বছর পর বাংলাকে বিভক্ত করা হয় এবং ঢাকা পূর্ববাংলা ও আসামের নবনির্মিত প্রদেশের রাজধানী হয়ে ওঠে। 1911 সালে বিভাজন বাতিলের পর এটি ঢাকা কলেজের প্রাঙ্গনে ব্যবহৃত হয় এবং 1921 সালে ঢাকা বিশ্ববিদ্যালয় প্রতিষ্ঠার পর বিশ্ববিদ্যালয় বিজ্ঞান বিভাগের অংশ হয়ে ওঠে, যা এখন পর্যন্ত চলছে। একটি প্রশস্ত এবং সাবধানে রক্ষণাবেক্ষণ করা বাগানে সজ্জিত, এই ডবল তক্তা ইটের ভবনের একটি বড় কেন্দ্রীয় হল, পূর্ব ও পশ্চিমের পাশের পাখিগুলি বিভিন্ন কক্ষগুলির সাথে এবং সবদিকে একটি ধারাবাহিক বারান্দা রয়েছে।\r\n\r\nঢাকার স্থাপত্যশিল্পের সেরা উদাহরণগুলির মধ্যে একটি, এটি ইউরোপীয় এবং মুগল উপাদানগুলির একটি সুখী মিশ্রন, বিশেষ করে উত্তরের প্রজেক্টিং মুখোমুখি অঞ্চলে দৃষ্টিগোচর হয়, যার মধ্যে ঘোড়া জুতা এবং কুস্তিযুক্ত উভয় খিলান রয়েছে।\r\n\r\nশৈলীটি আধুনিক প্রযুক্তির সাথে যৌথভাবে ঐতিহ্যবাহী শিল্প এবং মুগল রূপগুলি যেমন খিলান এবং গম্বুজগুলির মত বিশেষ করে পশ্চিমে ইসলামী বিশ্বের প্রবেশ করেছে। 1857 সালের সিপাহি বিদ্রোহের পর এবং ভারত সরাসরি ব্রিটিশ মুকুটের অধীনে মুগলদের সাথে সংযোগ স্থাপন করে বৈধতা কামনা করার পর এটি প্রকাশ্য ক্ষমতার ঢালাইয়ের চিহ্নটিকে চিহ্নিত করে। লাল বেলেপাথরের জন্য লাল রঙের রঙ, এবং অলঙ্কার বন্ধনী, গভীর আভ্যন্তরীণ এবং বিশেষত মধ্যবিত্ত অংশের গম্বুজবিশিষ্ট ছাদ (ছত্রিস) হ\'ল ফাতেহপুরের প্রাসাদ দুর্গতে ছোট কিন্তু সুপরিচিত দিওয়ান-ই-খাসের স্মরণীয়ভাবে স্মরণীয়। 1570 ও 1585 সালের মাঝামাঝি সময়ে সম্রাট আকবারের রাজধানী সিক্রি। উভয় শহরই কেবল নতুন রাজধানী ছিল না, তবে ফতেহপুর সিক্রি শৈলী সম্পর্কে ইচ্ছাকৃত পছন্দটি ব্যাখ্যা করা যেতে পারে যে, ব্রিটিশরা আকবরকে সকল মুগলদের সবচেয়ে জ্ঞানী ও সহনশীল হিসাবে গ্রহণ করেছিল। ভারতে তাদের নিজস্ব ভূমিকা আদর্শ।\r\n\r\nকার্জন হল ভাষা আন্দোলনের ইতিহাসে একটি বড় তাত্পর্য অর্জন করেছে। 1948 সালে এখানে ঢাকা বিশ্ববিদ্যালয়ের ছাত্ররা মুহাম্মাদ আলী জিন্নাহর ঘোষণাকে স্বীকার করে যে, উর্দু তখনই পাকিস্তানের সেই রাষ্ট্রীয় ভাষা হবে।', 'CURJON.jpg', '2019-02-23 23:54:39', '2019-02-23 23:54:39'),
(2, 'ঢাকা জাদুঘর', 'ঢাকা', '1856 সালের 1 লা নভেম্বর ঢাকায় একটি জাদুঘর জনসাধারণের চাহিদা বাড়ানোর বিষয়ে খবর প্রকাশ করে। কিন্তু উনিশ শতকের দিকে ব্রিটিশ ভারতের ভারপ্রাপ্ত ভাইসরয় লর্ড কারজন 1905 সালে বাংলার ভাগাভাগি করার পর ঢাকায় একটি জাদুঘর প্রতিষ্ঠার সুযোগ পান। এর ফলে ঢাকার ঐতিহাসিক শহর রাজধানী হয়ে ওঠে। পূর্ববাংলা ও আসামের নতুন প্রদেশ। যেহেতু শিলং মন্ত্রিসভার মুদ্রা ঢাকায় স্থানান্তরিত হতে চলেছে, তাই নতুন রাজধানী হে স্ট্যাপলেটন, মাননীয় সরকার নুমিসম্যান্টিস্ট, ঢাকায় একটি জাদুঘর স্থাপনের জন্য জনসাধারণের নির্দেশককে একটি চিঠি প্রস্তাব করেছিলেন। প্রস্তাবটি নতুন প্রদেশের গভর্নর স্যার ল্যান্সেলট হেরের মনোযোগে আনা হয়েছিল এবং একটি যাদুঘরের জন্য একটি স্থান নির্বাচন করার আদেশ দিয়েছিল। কিন্তু 1911 সালে বাংলার বিভাজন বাতিল করা হয় এবং ঢাকা রাজধানী হিসাবে অবস্থান হারিয়ে ফেলে। ফলস্বরূপ, ঢাকার একটি জাদুঘর স্থাপনের সিদ্ধান্ত পিছিয়ে পড়েছিল। তখন ঢাকার অভিজাতরা এগিয়ে আসেন এবং সক্রিয় আগ্রহের সাথে একটি যাদুঘর দাবি করেন।', 'mujiam.jpg', '2019-02-24 06:31:56', '2019-02-24 06:31:56'),
(3, 'আহসান মঞ্জিল', 'ঢাকা', 'আহসান মঞ্জিল, একটি স্থাপত্যকেন্দ্র, বাংলাদেশের অনেক ঐতিহাসিক ঘটনা দেখেছেন। ঊনবিংশ শতাব্দীর শেষ ভাগে পাকিস্তানের প্রাথমিক বছর পর্যন্ত, পূর্ববাংলার মুসলিম নেতৃত্ব এই প্রাসাদ থেকে আবির্ভূত হয়েছিল। ঢাকার নওয়াবরা প্রতিদিন পঞ্চায়েত (গ্রাম পরিষদ) প্রধান হিসাবে তাদের আদালতের বিষয়গুলি পরিচালনা করতেন। মুসলিম আলাদা পরিচয়পত্রের দৃঢ় বিশ্বাসী নওয়াব আহসানুল্লাহর উদ্যোগে এখানে অনেকেই কংগ্রেস বিরোধী বৈঠক করেন। ঢাকা সফররত প্রায় সকল ভাইসরয়ে, গভর্নর এবং লেফটেন্যান্ট গভর্নররা আহসান মঞ্জিলে কিছু সময় অতিবাহিত করেছিলেন।আহসান মঞ্জিল দেশের উল্লেখযোগ্য স্থাপত্যকেন্দ্রগুলির অন্যতম। 1 মিটার একটি উত্থাপিত প্ল্যাটফর্ম স্থাপিত, দুই তলা প্রাসাদ 125.4m 28.75m দ্বারা পরিমাপ। মাঠের উচ্চতা 5 মিটার এবং প্রথম তলায় 5.8 মিটার। প্রাসাদের উত্তরাঞ্চলীয় ও দক্ষিণ দিকের উভয় স্থলভাগের উচ্চতা রয়েছে। একটি খোলা প্রশস্ত সিঁড়ি দক্ষিণ পোর্টিকো থেকে নেমে এসেছে, যা সামনে বাগানের মধ্য দিয়ে নদীর তীরে বিস্তৃত। একবার সিঁড়ি সামনে বাগানে একটি ঝরনা ছিল, যদিও এটি আজ বিদ্যমান নেই। মেঝে উভয় প্রশস্ত উত্তর এবং দক্ষিণ বারান্দা semicircular arches উপর বিশ্রাম। মেঝে মার্বেল সঙ্গে আচ্ছাদিত করা হয়। প্রবেশ মূল্য : (টিকেট মূল্য আপডেট 23/11/2018। স্থানীয় পর্যটক - ২0 টাকা। সার্ক দেশ পর্যটক - 300 টাকা। অন্যান্য বিদেশী পর্যটক - 500 টাকা । ঘুরে দেখার জন্য: গ্রীষ্মকালীন ঋতু (এপ্রিল-সেপ্টেম্বর) 1. শনিবার - বুধবার: 10.30 মিনিট -5.30 অপরাহ্ন। 2. শুক্রবার: 3.00 বিকাল - 7.30 টা। 3. বৃহস্পতিবার: সাপ্তাহিক ছুটির দিন। সরকারি ছুটির সময় এবং ঈদের দিন ও পূর্ববর্তী দিনে আহসান মঞ্জিলবন্ধ থাকবে।', 'ah.jpg', '2019-02-24 07:01:47', '2019-02-24 07:01:47');

